import React, { useEffect, useState } from 'react';
import { Button, StyleSheet, TouchableOpacity, TextInput, FlatList, Image } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

import { Text, View } from './Themed';

const baseUrl = 'https://api.spoonacular.com/recipes';
const apiKey = '05f3e5cbe3194079a0484c97fa512c66';

const safeEncodeURIComponent = (raw) =>
{
    return encodeURIComponent(raw).replace(/'/g, "%27");
}


const placeholderText = "Type a list of ingredients (comma-seperated)";

const RecipeSearch = props =>
{
    return (
        <View>
        </View>
    );
}

const Recipes = props =>
{
    const [isLoading, setLoading] = useState(false);
    const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);

    const getRecipesFromQuery = async (query, numberOfResults) => {
        try {
            query = 'chicken, paprika';
            if (query && query.toString().length > 0) {
          const apiUrl = `${baseUrl}/complexSearch?apiKey=${apiKey}&query=${query}`;
          
            
          console.error(`formed URL: ${apiUrl}`);
          const response = await fetch(apiUrl);
          const json = await response.json();
          setResults(json.results);
          setLoading(false);
            }
        } catch (error) {
          console.error(error);
        }
    };

    useEffect(() => { setLoading(true); getRecipesFromQuery({query}, 5)}, []);

    return (
        <View>
        <TextInput
          style={styles.getStartedText}
          color={styles.textInput}
          onChangeText={setQuery}
          value={query}
          placeholder={placeholderText}
        />

        <Button title="Go!" style={styles.Button} />


        {isLoading ? <Text>Loading...</Text> :
        (
        <LinearGradient colors={["rgba(0,0,0,0.05)", "rgba(0,0,0,0)"]}>
            <FlatList
                data={results}
                keyExtractor={({ id }) => id.toString() }
                renderItem={({ item }) => <Text>{item.title}</Text>}
                renderItem={({ item }) => <Image source={{ uri: item.image.toString() }}></Image>} 
            />
        </LinearGradient>)}
        </View>
    );
};
export default Recipes;
  
const styles = StyleSheet.create({
    tinyLogo: {
      width: 50,
      height: 50,
    },
    getStartedText: {
      fontSize: 17,
      lineHeight: 24,
      textAlign: 'center',
    },
    textInput: {
        alignSelf: 'stretch',
      color: '#fff',
    },
    Button: {paddingTop: '10px'}
  });
