import * as WebBrowser from 'expo-web-browser';
import React, { useState } from 'react';
import { StyleSheet, TouchableOpacity, TextInput, FlatList, Image } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';


const [text, setText] = useState('');
const [results, setResults] = useState([]);
const [title, setTitle] = useState('');
const [recipeImageUri, setRecipeImageUri] = useState('');
const [missingIngredients, setMissingIngredients] = useState(null);

const placeholderText = "Type a list of ingredients (comma-seperated)";

const RecipeSearch = props =>
{
    return (
        <View>
        <Text
          style={styles.getStartedText}
          lightColor="rgba(0,0,0,0.8)"
          darkColor="rgba(255,255,255,0.8)">
          Search for Recipes:
        </Text>

        <TextInput
          style={styles.getStartedText}
          onChangeText={setText}
          value={text}
          placeholder={placeholderText}
        />
        </View>
    );
}

const Recipes = props =>
{
    return (
        <LinearGradient colors={["rgba(0,0,0,0.05)", "rgba(0,0,0,0)"]}>
            <FlatList
                data={results}
                keyExtractor={({ id }) => id.toString() }
                renderItem={({ item }) => <Text>{item.title}</Text>} />
        </LinearGradient>
    );
};

export default Recipes;
